import { useInfiniteQuery, useQuery } from 'react-query'
import { View,ActivityIndicator, Text, SafeAreaView, ScrollView, StyleSheet , TouchableOpacity} from 'react-native';
import React from 'react';

import * as filmService from '../service/film.service';


export default function ListFilm() {

  const { isLoading, isError, data, error } = useQuery('list-film', filmService.findAll)

  if( isLoading ){
    return(
      <>
        <View style={styles.containspinner}>
          <ActivityIndicator size="large" color="#0000ff" />
        </View>
      </>

    )
  }

  if( isError ){
    return(
      <View>
        <Text>error: {error.message}</Text> 
      </View>
    )
  }

  return (
    <>
      <SafeAreaView style={styles.container}>
        <ScrollView showsHorizontalScrollIndicator={false} showsVerticalScrollIndicator={false}>
          <View style={styles.scrollView}>
              {data && data.map(movie =>(
                <TouchableOpacity>
                  <Text key={movie.id} >{movie.titre}</Text>
                </TouchableOpacity>
              ))}
          </View>

        <Text>{}</Text>
        </ScrollView>
      </SafeAreaView>
    </>
  );
}

const styles = StyleSheet.create({
  containspinner:{
    flex:1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  container: {
    flex: 1,
  },
  scrollView: {
    flexDirection: "row",
    flexWrap: "wrap"
  },
  button: {
    alignItems: "center",
    backgroundColor: "#DDDDDD",
    padding: 10
  },
});
