import React, {useState, useEffect} from 'react'
import { View, Text, SafeAreaView, TextInput, StyleSheet} from 'react-native'
import { useNavigation } from '@react-navigation/native';
import Boutton from '../components/boutton';
import FilmImage from '../components/filmImage';
import * as filmService from '../service/film.service';
import { getUserStorage } from '../helper/user.storage';




const AddFilm = () => {

    const [titre, setTitre] = useState("");
    const [description, setDescription] = useState("");
    const [note, setNote] = useState("");
    const [url, setUrl] = useState("");
    const [data, setData] = useState([])
    const { id } = getUserStorage();
    const [isLogin, setIsLogin] = useState(false);
    const navigation = useNavigation();

    const item = (<Text>Save</Text>) 

    const onChangeIsLogin = () =>{
        setIsLogin(true)
    }

    const save = () => {
        const data =  filmService.create({
            titre: titre,
            description: description,
            note: note,
            url: url,
            userId: id
        })
        navigation.navigate('ListFilm');

    }

    useEffect(()=>{
        filmService.findbyTitle(titre).then(data =>{
            console.log(data)
          if(data){
              setData(data)
          }  
        })
    },[titre])

    const ImagerShearh = async () => await filmService.findTitre(titre)


    return (
        <SafeAreaView>
            <Text style={styles.text}>Titre :</Text>
            <TextInput onChangeText={setTitre} value={titre} style={styles.input}/>
            <Text style={styles.text}>Description :</Text>
            <TextInput onChangeText={setDescription} value={description} style={styles.input}/>
            <Text style={styles.text}>Note sur /10 :</Text>
            <TextInput onChangeText={setNote} value={note} style={styles.input}/>
            <Text style={styles.text}>url:</Text>
            <FilmImage data={data} url={url} setUrl={setUrl}/>
            <Boutton item={item} style={styles.button} onPress={save}/>
        </SafeAreaView>
    )
}

export default AddFilm;

const styles = StyleSheet.create({
    input: {
      height: 40,
      margin: 12,
      borderWidth: 1,
      padding: 10,
    },
    text: {
        marginLeft: 10,
        marginTop: 10,
    },
    checkbox: {
        width: 64,
        height: 64
    }
  })
